        "$:/plugins/twpub/b444daeafa3e6711/text/000002803": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002803",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Claw most uncanny; quoth they agreeing,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002804": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002804",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a> <span class=\"sidenote\">No sword will harm the monster.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002805": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002805",
