        "$:/plugins/twpub/b444daeafa3e6711/text/000002885": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002885",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A banner embossed, burnie and helmet;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002886": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002886",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002887": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002887",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Many men saw then a song-famous weapon</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002888": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002888",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002889": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002889",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Borne ’fore the hero. Beowulf drank of</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002890": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002890",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002891": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002891",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The cup in the building; that treasure-bestowing</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002892": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002892",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">35</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002893": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002893",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He needed not blush for in battle-men’s presence.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002894": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002894",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Four handsomer gifts were never presented.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002895": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002895",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Ne’er heard I that many men on the ale-bench</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002896": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002896",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002897": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002897",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In friendlier fashion to their fellows presented</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002898": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002898",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002899": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002899",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Four bright jewels with gold-work embellished.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002900": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002900",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002901": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002901",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Round the roof of the helmet a head-guarder outside</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002902": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002902",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">40</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002903": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002903",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Braided with wires, with bosses was furnished,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002904": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002904",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002905": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002905",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That swords-for-the-battle fight-hardened might fail</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002906": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002906",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002907": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002907",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Boldly to harm him, when the hero proceeded</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002908": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002908",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Hrothgar commands that eight finely caparisoned steeds be brought to Beowulf.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002909": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002909",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Forth against foemen. The defender of earls then</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002910": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002910",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002911": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002911",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Commanded that eight steeds with bridles</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002912": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002912",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">45</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002913": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002913",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Gold-plated, gleaming, be guided to hallward,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002914": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002914",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002915": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002915",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Inside the building; on one of them stood then</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002916": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002916",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002917": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002917",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">An art-broidered saddle embellished with jewels;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002918": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002918",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002919": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002919",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Twas the sovereign’s seat, when the son of King Healfdene</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002920": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002920",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002921": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002921",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Was pleased to take part in the play of the edges;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002922": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002922",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">50</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002923": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002923",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The famous one’s valor ne’er failed at the front when</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002924": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002924",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002925": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002925",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Slain ones were bowing. And to Beowulf granted</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002926": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002926",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002927": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002927",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The prince of the Ingwins, power over both,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002928": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002928",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002929": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002929",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">O’er war-steeds and weapons; bade him well to enjoy them.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002930": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002930",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002931": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002931",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In so manly a manner the mighty-famed chieftain,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002932": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002932",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a> <span class=\"linenum\">55</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002933": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002933",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hoard-ward of heroes, with horses and jewels</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002934": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002934",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002935": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002935",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">War-storms requited, that none e’er condemneth</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002936": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002936",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002937": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002937",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Who willeth to tell truth with full justice.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002938": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002938",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002939": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002939",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002940": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002940",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002825\">[1]</$link> Kl. suggests ‘<span class=\"ang\">hroden</span>’ for ‘<span class=\"ang\">háten</span>,’ and renders: <i>Then quickly was Heorot adorned within, with hands bedecked</i>.—B. suggests ‘<span class=\"ang\">gefrætwon</span>’ instead of ‘<span class=\"ang\">gefrætwod</span>,’ and renders: <i>Then was it commanded to adorn Heorot within quickly with hands</i>.—The former has the advantage of affording a parallel to ‘<span class=\"ang\">gefrætwod</span>’: both have the disadvantage of altering the text.</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002941": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002941",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002942": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002942",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002849\">[2]</$link> The passage 1005-1009 seems to be hopeless. One difficult point is to find a subject for ‘<span class=\"ang\">gesacan</span>.’ Some say ‘he’; others supply ‘each,’ <i>i.e., every soul-bearer … must gain the inevitable place</i>. The genitives in this case are partitive.—If ‘he’ be subj., the genitives are dependent on ‘<span class=\"ang\">gearwe</span>’ (= prepared).—The ‘he’ itself is disputed, some referring it to Grendel; but B. takes it as involved in the parenthesis.</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002943": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002943",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002944": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002944",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002945": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002945",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\">\n</div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002946": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002946",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002947": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002947",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2 class=\"num\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000000081\">XVII.</$link></h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002948": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002948",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002949": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002949",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2>BANQUET (<i>continued</i>).—THE SCOP’S SONG OF FINN AND HNÆF.</h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002950": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002950",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Each of Beowulf’s companions receives a costly gift.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002951": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002951",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And the atheling of earlmen to each of the heroes</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002952": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002952",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002953": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002953",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Who the ways of the waters went with Beowulf,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002954": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002954",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002955": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002955",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A costly gift-token gave on the mead-bench,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002956": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002956",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002957": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002957",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Offered an heirloom, and ordered that that man</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002958": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002958",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The warrior killed by Grendel is to be paid for in gold.</span> <span class=\"linenum\">5</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002959": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002959",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">With gold should be paid for, whom Grendel had erstwhile</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002960": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002960",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002961": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002961",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Wickedly slaughtered, as he more of them had done</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002962": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002962",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002963": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002963",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Had far-seeing God and the mood of the hero</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002964": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002964",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002965": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002965",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The fate not averted: the Father then governed</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002966": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002966",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002967": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002967",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">All of the earth-dwellers, as He ever is doing;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002968": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002968",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">10</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002969": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002969",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hence insight for all men is everywhere fittest,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002970": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002970",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002971": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002971",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Forethought of spirit! much he shall suffer</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002972": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002972",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002973": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002973",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of lief and of loathsome who long in this present</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002974": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002974",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002975": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002975",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Useth the world in this woful existence.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002976": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002976",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002977": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002977",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">There was music and merriment mingling together</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002978": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002978",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Hrothgar’s scop recalls events in the reign of his lord’s father.</span> <span class=\"linenum\">15</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002979": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002979",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Touching Healfdene’s leader; the joy-wood was fingered,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002980": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002980",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002981": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002981",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Measures recited, when the singer of Hrothgar</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002982": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002982",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002983": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002983",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">On mead-bench should mention the merry hall-joyance</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002984": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002984",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002985": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002985",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of the kinsmen of Finn, when onset surprised them:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002986": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002986",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Hnæf, the Danish general, is treacherously attacked while staying at Finn’s castle.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002987": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002987",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">“The Half-Danish hero, Hnæf of the Scyldings,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002988": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002988",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">20</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002989": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002989",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">On the field of the Frisians was fated to perish.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002990": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002990",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002991": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002991",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Sure Hildeburg needed not mention approving</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002992": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002992",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002993": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002993",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The faith of the Jutemen: though blameless entirely,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002994": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002994",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Queen Hildeburg is not only wife of Finn, but a kinswoman of the murdered Hnæf.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002995": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002995",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">When shields were shivered she was shorn of her darlings,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002996": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002996",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002997": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002997",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of bairns and brothers: they bent to their fate</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002998": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002998",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">25</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002999": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002999",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">With war-spear wounded; woe was that woman.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003000": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003000",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003001": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003001",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Not causeless lamented the daughter of Hoce</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003002": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003002",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003003": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003003",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The decree of the Wielder when morning-light came and</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003004": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003004",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003005": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003005",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">She was able ’neath heaven to behold the destruction</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003006": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003006",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003007": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003007",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of brothers and bairns, where the brightest of earth-joys</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003008": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003008",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Finn’s force is almost exterminated.</span> <span class=\"linenum\">30</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003009": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003009",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">She had hitherto had: all the henchmen of Finn</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003010": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003010",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003011": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003011",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">War had offtaken, save a handful remaining,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003012": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003012",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003013": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003013",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That he nowise was able to offer resistance<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000003094\">1</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003014": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003014",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Hengest succeeds Hnæf as Danish general.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003015": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003015",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To the onset of Hengest in the parley of battle,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003016": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003016",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003017": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003017",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Nor the wretched remnant to rescue in war from</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003018": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003018",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">35</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003019": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003019",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The earl of the atheling; but they offered conditions,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003020": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003020",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Compact between the Frisians and the Danes.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003021": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003021",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Another great building to fully make ready,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003022": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003022",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003023": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003023",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A hall and a high-seat, that half they might rule with</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003024": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003024",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003025": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003025",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The sons of the Jutemen, and that Folcwalda’s son would</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003026": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003026",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003027": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003027",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Day after day the Danemen honor</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003028": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003028",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">40</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003029": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003029",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">When gifts were giving, and grant of his ring-store</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003030": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003030",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003031": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003031",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To Hengest’s earl-troop ever so freely,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003032": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003032",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003033": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003033",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of his gold-plated jewels, as he encouraged the Frisians</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003034": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003034",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Equality of gifts agreed on.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003035": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003035",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">On the bench of the beer-hall. On both sides they swore then</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003036": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003036",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003037": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003037",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A fast-binding compact; Finn unto Hengest</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003038": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003038",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">45</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003039": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003039",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">With no thought of revoking vowed then most solemnly</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003040": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003040",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003041": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003041",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The woe-begone remnant well to take charge of,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003042": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003042",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003043": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003043",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His Witan advising; the agreement should no one</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003044": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003044",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003045": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003045",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">By words or works weaken and shatter,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003046": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003046",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003047": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003047",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">By artifice ever injure its value,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003048": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003048",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">50</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003049": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003049",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Though reaved of their ruler their ring-giver’s slayer</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003050": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003050",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003051": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003051",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">They followed as vassals, Fate so requiring:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003052": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003052",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">No one shall refer to old grudges.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003053": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003053",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Then if one of the Frisians the quarrel should speak of</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003054": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003054",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003055": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003055",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In tones that were taunting, terrible edges</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003056": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003056",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003057": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003057",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Should cut in requital. Accomplished the oath was,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003058": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003058",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">55</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003059": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003059",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And treasure of gold from the hoard was uplifted.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003060": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003060",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Danish warriors are burned on a funeral-pyre.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003061": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003061",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The best of the Scylding braves was then fully</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003062": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003062",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003063": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003063",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Prepared for the pile; at the pyre was seen clearly</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003064": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003064",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003065": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003065",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The blood-gory burnie, the boar with his gilding,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003066": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003066",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003067": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003067",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The iron-hard swine, athelings many</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003068": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003068",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">60</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003069": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003069",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Fatally wounded; no few had been slaughtered.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003070": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003070",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003071": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003071",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hildeburg bade then, at the burning of Hnæf,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003072": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003072",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a> <span class=\"sidenote\">Queen Hildeburg has her son burnt along with Hnæf.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003073": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003073",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The bairn of her bosom to bear to the fire,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003074": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003074",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003075": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003075",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That his body be burned and borne to the pyre.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003076": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003076",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003077": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003077",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The woe-stricken woman wept on his shoulder,<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000003096\">2</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003078": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003078",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">65</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003079": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003079",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In measures lamented; upmounted the hero.<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000003098\">3</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003080": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003080",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003081": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003081",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The greatest of dead-fires curled to the welkin,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003082": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003082",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003083": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003083",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">On the hill’s-front crackled; heads were a-melting,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003084": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003084",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003085": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003085",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Wound-doors bursting, while the blood was a-coursing</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003086": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003086",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003087": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003087",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">From body-bite fierce. The fire devoured them,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003088": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003088",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">70</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003089": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003089",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Greediest of spirits, whom war had offcarried</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003090": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003090",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003091": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003091",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">From both of the peoples; their bravest were fallen.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003092": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003092",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003093": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003093",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003094": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003094",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000003013\">[1]</$link> For 1084, R. suggests ‘<span class=\"ang\">wiht Hengeste wið gefeohtan</span>.’—K. suggests ‘<span class=\"ang\">wið Hengeste wiht gefeohtan</span>.’ Neither emendation would make any essential change in the translation.</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003095": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003095",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003096": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003096",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000003077\">[2]</$link> The separation of adjective and noun by a phrase (cf. v. 1118) being very unusual, some scholars have put ‘<span class=\"ang\">earme on eaxle</span>’ with the foregoing lines, inserting a semicolon after ‘<span class=\"ang\">eaxle</span>.’ In this case ‘<span class=\"ang\">on eaxe</span>’ (<i>i.e.</i>, on the ashes, cinders) is sometimes read, and this affords a parallel to ‘<span class=\"ang\">on bæl</span>.’ Let us hope that a satisfactory rendering shall yet be reached without resorting to any tampering with the text, such as Lichtenheld proposed: ‘<span class=\"ang\">earme ides on eaxle gnornode</span>.’</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003097": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003097",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003098": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003098",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000003079\">[3]</$link> For ‘<span class=\"ang\">gúð-rinc</span>,’ ‘<span class=\"ang\">gúð-réc</span>,’ <i>battle-smoke</i>, has been suggested.</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003099": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003099",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003100": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003100",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003101": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003101",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\">\n</div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003102": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003102",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003103": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003103",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2 class=\"num\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000000085\">XVIII.</$link></h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003104": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003104",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003105": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003105",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2>THE FINN EPISODE (<i>continued</i>).—THE BANQUET CONTINUES.</h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003106": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003106",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The survivors go to Friesland, the home of Finn.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003107": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003107",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">“Then the warriors departed to go to their dwellings,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003108": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003108",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003109": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003109",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Reaved of their friends, Friesland to visit,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003110": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003110",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003111": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003111",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Their homes and high-city. Hengest continued</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003112": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003112",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Hengest remains there all winter, unable to get away.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003113": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003113",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Biding with Finn the blood-tainted winter,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003114": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003114",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">5</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003115": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003115",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Wholly unsundered;<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000003242\">1</$link></sup> of fatherland thought he</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003116": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003116",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003117": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003117",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Though unable to drive the ring-stemmèd vessel</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003118": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003118",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003119": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003119",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">O’er the ways of the waters; the wave-deeps were tossing,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003120": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003120",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003121": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003121",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Fought with the wind; winter in ice-bonds</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003122": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003122",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003123": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003123",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Closed up the currents, till there came to the dwelling</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003124": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003124",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">10</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003125": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003125",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A year in its course, as yet it revolveth,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003126": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003126",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003127": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003127",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">If season propitious one alway regardeth,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003128": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003128",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003129": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003129",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">World-cheering weathers. Then winter was gone,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003130": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003130",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003131": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003131",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Earth’s bosom was lovely; the exile would get him,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003132": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003132",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">He devises schemes of vengeance.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003133": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003133",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The guest from the palace; on grewsomest vengeance</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003134": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003134",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">15</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003135": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003135",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He brooded more eager than on oversea journeys,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003136": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003136",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003137": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003137",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Whe’r onset-of-anger he were able to ’complish,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003138": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003138",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003139": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003139",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The bairns of the Jutemen therein to remember.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003140": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003140",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003141": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003141",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Nowise refused he the duties of liegeman</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003142": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003142",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003143": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003143",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">When Hun of the Frisians the battle-sword Láfing,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003144": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003144",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">20</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003145": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003145",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Fairest of falchions, friendly did give him:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003146": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003146",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003147": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003147",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Its edges were famous in folk-talk of Jutland.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003148": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003148",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003149": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003149",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And savage sword-fury seized in its clutches</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003150": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003150",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003151": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003151",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Bold-mooded Finn where he bode in his palace,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003152": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003152",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Guthlaf and Oslaf revenge Hnæf’s slaughter.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003153": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003153",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">When the grewsome grapple Guthlaf and Oslaf</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003154": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003154",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">25</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003155": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003155",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Had mournfully mentioned, the mere-journey over,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003156": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003156",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003157": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003157",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">For sorrows half-blamed him; the flickering spirit</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003158": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003158",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003159": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003159",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Could not bide in his bosom. Then the building was covered<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000003244\">2</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003160": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003160",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Finn is slain.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003161": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003161",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">With corpses of foemen, and Finn too was slaughtered,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003162": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003162",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003163": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003163",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The king with his comrades, and the queen made a prisoner.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003164": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003164",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The jewels of Finn, and his queen are carried away by the Danes.</span> <span class=\"linenum\">30</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003165": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003165",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The troops of the Scyldings bore to their vessels</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003166": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003166",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003167": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003167",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">All that the land-king had in his palace,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003168": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003168",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003169": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003169",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Such trinkets and treasures they took as, on searching,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003170": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003170",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003171": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003171",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">At Finn’s they could find. They ferried to Daneland</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003172": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003172",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003173": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003173",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The excellent woman on oversea journey,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003174": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003174",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The lay is concluded, and the main story is resumed.</span> <span class=\"linenum\">35</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003175": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003175",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Led her to their land-folk.” The lay was concluded,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003176": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003176",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003177": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003177",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The gleeman’s recital. Shouts again rose then,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003178": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003178",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003179": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003179",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Bench-glee resounded, bearers then offered</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003180": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003180",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Skinkers carry round the beaker.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003181": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003181",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Wine from wonder-vats. Wealhtheo advanced then</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003182": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003182",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003183": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003183",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Going ’neath gold-crown, where the good ones were seated</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003184": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003184",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a> <span class=\"sidenote\">Queen Wealhtheow greets Hrothgar, as he sits beside Hrothulf, his nephew.</span> <span class=\"linenum\">40</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003185": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003185",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Uncle and nephew; their peace was yet mutual,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003186": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003186",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003187": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003187",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">True each to the other. And Unferth the spokesman</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003188": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003188",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003189": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003189",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Sat at the feet of the lord of the Scyldings:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003190": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003190",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003191": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003191",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Each trusted his spirit that his mood was courageous,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003192": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003192",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003193": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003193",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Though at fight he had failed in faith to his kinsmen.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003194": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003194",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">45</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003195": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003195",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Said the queen of the Scyldings: “My lord and protector,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003196": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003196",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003197": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003197",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Treasure-bestower, take thou this beaker;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003198": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003198",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003199": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003199",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Joyance attend thee, gold-friend of heroes,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003200": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003200",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Be generous to the Geats.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003201": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003201",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And greet thou the Geatmen with gracious responses!</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003202": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003202",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003203": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003203",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">So ought one to do. Be kind to the Geatmen,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003204": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003204",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">50</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003205": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003205",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In gifts not niggardly; anear and afar now</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003206": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003206",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003207": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003207",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Peace thou enjoyest. Report hath informed me</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003208": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003208",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003209": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003209",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Thou’lt have for a bairn the battle-brave hero.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003210": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003210",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003211": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003211",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Now is Heorot cleansèd, ring-palace gleaming;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003212": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003212",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Have as much joy as possible in thy hall, once more purified.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003213": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003213",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Give while thou mayest many rewards,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003214": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003214",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">55</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003215": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003215",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And bequeath to thy kinsmen kingdom and people,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003216": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003216",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003217": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003217",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">On wending thy way to the Wielder’s splendor.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003218": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003218",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003219": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003219",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">I know good Hrothulf, that the noble young troopers</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003220": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003220",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">I know that Hrothulf will prove faithful if he survive thee.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003221": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003221",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He’ll care for and honor, lord of the Scyldings,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003222": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003222",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003223": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003223",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">If earth-joys thou endest earlier than he doth;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003224": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003224",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">60</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003225": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003225",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">I reckon that recompense he’ll render with kindness</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003226": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003226",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003227": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003227",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Our offspring and issue, if that all he remember,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003228": {
            "role": "text",
