        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003543": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003543",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Hrothgar laments the death of Æschere, his shoulder-companion.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003544": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003544",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hrothgar rejoined, helm of the Scyldings:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003545": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003545",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003546": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003546",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">“Ask not of joyance! Grief is renewed to</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003547": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003547",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003548": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003548",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The folk of the Danemen. Dead is Æschere,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003549": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003549",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003550": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003550",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Yrmenlaf’s brother, older than he,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003551": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003551",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">5</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003552": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003552",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">My true-hearted counsellor, trusty adviser,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003553": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003553",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003554": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003554",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Shoulder-companion, when fighting in battle</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003555": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003555",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003556": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003556",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Our heads we protected, when troopers were clashing,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003557": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003557",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">He was my ideal hero.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003558": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003558",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And heroes were dashing; such an earl should be ever,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003559": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003559",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003560": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003560",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">An erst-worthy atheling, as Æschere proved him.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003561": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003561",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">10</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003562": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003562",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The flickering death-spirit became in Heorot</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003563": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003563",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003564": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003564",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His hand-to-hand murderer; I can not tell whither</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003565": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003565",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003566": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003566",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The cruel one turned in the carcass exulting,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003567": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003567",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a> <span class=\"sidenote\">This horrible creature came to avenge Grendel’s death.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003568": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003568",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">By cramming discovered.<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000003667\">1</$link></sup> The quarrel she wreaked then,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003569": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003569",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003570": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003570",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That last night igone Grendel thou killedst</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003571": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003571",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">15</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003572": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003572",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In grewsomest manner, with grim-holding clutches,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003573": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003573",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003574": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003574",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Since too long he had lessened my liege-troop and wasted</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003575": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003575",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003576": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003576",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">My folk-men so foully. He fell in the battle</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003577": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003577",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003578": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003578",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">With forfeit of life, and another has followed,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003579": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003579",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003580": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003580",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A mighty crime-worker, her kinsman avenging,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003581": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003581",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">20</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003582": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003582",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And henceforth hath ‘stablished her hatred unyielding,<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000003669\">2</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003583": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003583",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003584": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003584",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">As it well may appear to many a liegeman,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003585": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003585",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003586": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003586",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Who mourneth in spirit the treasure-bestower,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003587": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003587",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003588": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003588",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Her heavy heart-sorrow; the hand is now lifeless</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003589": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003589",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003590": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003590",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Which<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000003671\">3</$link></sup> availed you in every wish that you cherished.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003591": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003591",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">I have heard my vassals speak of these two uncanny monsters who lived in the moors.</span> <span class=\"linenum\">25</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003592": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003592",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Land-people heard I, liegemen, this saying,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003593": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003593",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003594": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003594",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Dwellers in halls, they had seen very often</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003595": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003595",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003596": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003596",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A pair of such mighty march-striding creatures,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003597": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003597",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003598": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003598",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Far-dwelling spirits, holding the moorlands:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003599": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003599",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003600": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003600",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">One of them wore, as well they might notice,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003601": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003601",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">30</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003602": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003602",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The image of woman, the other one wretched</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003603": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003603",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003604": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003604",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In guise of a man wandered in exile,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003605": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003605",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003606": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003606",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Except he was huger than any of earthmen;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003607": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003607",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003608": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003608",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Earth-dwelling people entitled him Grendel</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003609": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003609",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003610": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003610",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In days of yore: they know not their father,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003611": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003611",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">35</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003612": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003612",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Whe’r ill-going spirits any were borne him</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003613": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003613",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The inhabit the most desolate and horrible places.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003614": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003614",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Ever before. They guard the wolf-coverts,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003615": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003615",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003616": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003616",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Lands inaccessible, wind-beaten nesses,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003617": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003617",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003618": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003618",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Fearfullest fen-deeps, where a flood from the mountains</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003619": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003619",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003620": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003620",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Neath mists of the nesses netherward rattles,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003621": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003621",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">40</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003622": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003622",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The stream under earth: not far is it henceward</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003623": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003623",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003624": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003624",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Measured by mile-lengths that the mere-water standeth,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003625": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003625",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003626": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003626",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Which forests hang over, with frost-whiting covered,<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000003673\">4</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003627": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003627",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003628": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003628",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A firm-rooted forest, the floods overshadow.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003629": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003629",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003630": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003630",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">There ever at night one an ill-meaning portent</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003631": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003631",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">45</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003632": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003632",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A fire-flood may see; ’mong children of men</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003633": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003633",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003634": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003634",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">None liveth so wise that wot of the bottom;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003635": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003635",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003636": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003636",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Though harassed by hounds the heath-stepper seek for,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003637": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003637",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Even the hounded deer will not seek refuge in these uncanny regions.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003638": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003638",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Fly to the forest, firm-antlered he-deer,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003639": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003639",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003640": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003640",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Spurred from afar, his spirit he yieldeth,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003641": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003641",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">50</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003642": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003642",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His life on the shore, ere in he will venture</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003643": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003643",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003644": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003644",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To cover his head. Uncanny the place is:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003645": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003645",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003646": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003646",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Thence upward ascendeth the surging of waters,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003647": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003647",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003648": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003648",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Wan to the welkin, when the wind is stirring</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003649": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003649",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003650": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003650",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The weathers unpleasing, till the air groweth gloomy,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003651": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003651",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">To thee only can I look for assistance.</span> <span class=\"linenum\">55</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003652": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003652",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And the heavens lower. Now is help to be gotten</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003653": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003653",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003654": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003654",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">From thee and thee only! The abode thou know’st not,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003655": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003655",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003656": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003656",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The dangerous place where thou’rt able to meet with</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003657": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003657",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003658": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003658",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The sin-laden hero: seek if thou darest!</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003659": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003659",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003660": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003660",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">For the feud I will fully fee thee with money,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003661": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003661",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">60</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003662": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003662",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">With old-time treasure, as erstwhile I did thee,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003663": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003663",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003664": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003664",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">With well-twisted jewels, if away thou shalt get thee.”</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003665": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003665",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003666": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003666",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003667": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003667",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000003568\">[1]</$link> For ‘<span class=\"ang\">gefrægnod</span>’ (1334), K. and t.B. suggest ‘<span class=\"ang\">gefægnod</span>,’ rendering ‘<i>rejoicing in her fill</i>.’ This gives a parallel to ‘<span class=\"ang\">æse wlanc</span>’ (1333).</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003668": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003668",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003669": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003669",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000003582\">[2]</$link> The line ‘And … yielding,’ B. renders: <i>And she has performed a deed of blood-vengeance whose effect is far-reaching</i>.</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003670": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003670",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003671": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003671",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000003590\">[3]</$link> ‘<span class=\"ang\">Sé Þe</span>’ (1345) is an instance of masc. rel. with fem. antecedent. So v. 1888, where ‘<span class=\"ang\">sé Þe</span>’ refers to ‘<span class=\"ang\">yldo</span>.’</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003672": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003672",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003673": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003673",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000003626\">[4]</$link> For ‘<span class=\"ang\">hrímge</span>’ in the H.-So. edition, Gr. and others read ‘<span class=\"ang\">hrínde</span>’ (=<span class=\"ang\">hrínende</span>), and translate: <i>which rustling forests overhang</i>.</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003674": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003674",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003675": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003675",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003676": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003676",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\">\n</div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003677": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003677",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003678": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003678",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2 class=\"num\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000000095\">XXII.</$link></h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003679": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003679",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003680": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003680",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2>BEOWULF SEEKS GRENDEL’S MOTHER.</h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003681": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003681",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003682": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003682",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Beowulf answered, Ecgtheow’s son:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003683": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003683",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf exhorts the old king to arouse himself for action.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003684": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003684",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">“Grieve not, O wise one! for each it is better,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003685": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003685",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003686": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003686",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His friend to avenge than with vehemence wail him;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003687": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003687",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003688": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003688",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Each of us must the end-day abide of</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003689": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003689",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">5</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003690": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003690",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His earthly existence; who is able accomplish</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003691": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003691",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003692": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003692",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Glory ere death! To battle-thane noble</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003693": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003693",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003694": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003694",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Lifeless lying, ’tis at last most fitting.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003695": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003695",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003696": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003696",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Arise, O king, quick let us hasten</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003697": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003697",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003698": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003698",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To look at the footprint of the kinsman of Grendel!</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003699": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003699",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">10</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003700": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003700",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">I promise thee this now: to his place he’ll escape not,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003701": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003701",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003702": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003702",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To embrace of the earth, nor to mountainous forest,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003703": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003703",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003704": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003704",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Nor to depths of the ocean, wherever he wanders.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003705": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003705",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003706": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003706",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Practice thou now patient endurance</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003707": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003707",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003708": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003708",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of each of thy sorrows, as I hope for thee soothly!”</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003709": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003709",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Hrothgar rouses himself. His horse is brought.</span> <span class=\"linenum\">15</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003710": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003710",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Then up sprang the old one, the All-Wielder thanked he,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003711": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003711",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003712": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003712",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Ruler Almighty, that the man had outspoken.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003713": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003713",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003714": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003714",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Then for Hrothgar a war-horse was decked with a bridle,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003715": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003715",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003716": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003716",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Curly-maned courser. The clever folk-leader</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003717": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003717",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">They start on the track of the female monster.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003718": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003718",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Stately proceeded: stepped then an earl-troop</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003719": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003719",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">20</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003720": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003720",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of linden-wood bearers. Her footprints were seen then</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003721": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003721",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003722": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003722",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Widely in wood-paths, her way o’er the bottoms,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003723": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003723",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003724": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003724",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Where she faraway fared o’er fen-country murky,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003725": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003725",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003726": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003726",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Bore away breathless the best of retainers</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003727": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003727",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003728": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003728",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Who pondered with Hrothgar the welfare of country.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003729": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003729",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">25</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003730": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003730",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The son of the athelings then went o’er the stony,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003731": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003731",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003732": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003732",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Declivitous cliffs, the close-covered passes,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003733": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003733",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003734": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003734",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Narrow passages, paths unfrequented,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003735": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003735",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003736": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003736",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Nesses abrupt, nicker-haunts many;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003737": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003737",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003738": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003738",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">One of a few of wise-mooded heroes,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003739": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003739",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">30</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003740": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003740",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He onward advanced to view the surroundings,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003741": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003741",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003742": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003742",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Till he found unawares woods of the mountain</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003743": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003743",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003744": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003744",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">O’er hoar-stones hanging, holt-wood unjoyful;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003745": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003745",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003746": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003746",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The water stood under, welling and gory.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003747": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003747",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003748": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003748",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Twas irksome in spirit to all of the Danemen,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003749": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003749",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">35</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003750": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003750",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Friends of the Scyldings, to many a liegeman</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003751": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003751",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The sight of Æschere’s head causes them great sorrow.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003752": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003752",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Sad to be suffered, a sorrow unlittle</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003753": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003753",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003754": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003754",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To each of the earlmen, when to Æschere’s head they</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003755": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003755",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003756": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003756",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Came on the cliff. The current was seething</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003757": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003757",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003758": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003758",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">With blood and with gore (the troopers gazed on it).</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003759": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003759",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">40</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003760": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003760",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The horn anon sang the battle-song ready.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003761": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003761",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003762": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003762",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The troop were all seated; they saw ’long the water then</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003763": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003763",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The water is filled with serpents and sea-dragons.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003764": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003764",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Many a serpent, mere-dragons wondrous</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003765": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003765",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003766": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003766",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Trying the waters, nickers a-lying</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003767": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003767",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003768": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003768",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">On the cliffs of the nesses, which at noonday full often</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003769": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003769",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">45</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003770": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003770",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Go on the sea-deeps their sorrowful journey,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003771": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003771",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003772": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003772",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Wild-beasts and wormkind; away then they hastened</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003773": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003773",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">One of them is killed by Beowulf.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003774": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003774",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hot-mooded, hateful, they heard the great clamor,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003775": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003775",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003776": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003776",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The war-trumpet winding. One did the Geat-prince</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003777": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003777",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003778": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003778",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Sunder from earth-joys, with arrow from bowstring,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003779": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003779",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">50</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003780": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003780",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">From his sea-struggle tore him, that the trusty war-missile</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003781": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003781",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The dead beast is a poor swimmer</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003782": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003782",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Pierced to his vitals; he proved in the currents</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003783": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003783",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003784": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003784",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Less doughty at swimming whom death had offcarried.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003785": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003785",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003786": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003786",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Soon in the waters the wonderful swimmer</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003787": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003787",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003788": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003788",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Was straitened most sorely with sword-pointed boar-spears,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003789": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003789",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">55</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003790": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003790",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Pressed in the battle and pulled to the cliff-edge;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003791": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003791",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003792": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003792",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The liegemen then looked on the loath-fashioned stranger.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003793": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003793",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf prepares for a struggle with the monster.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003794": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003794",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Beowulf donned then his battle-equipments,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003795": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003795",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003796": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003796",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Cared little for life; inlaid and most ample,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003797": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003797",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003798": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003798",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The hand-woven corslet which could cover his body,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003799": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003799",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">60</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003800": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003800",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Must the wave-deeps explore, that war might be powerless</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003801": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003801",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003802": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003802",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To harm the great hero, and the hating one’s grasp might</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003803": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003803",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003804": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003804",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Not peril his safety; his head was protected</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003805": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003805",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003806": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003806",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">By the light-flashing helmet that should mix with the bottoms,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003807": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003807",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003808": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003808",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Trying the eddies, treasure-emblazoned,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003809": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003809",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">65</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003810": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003810",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Encircled with jewels, as in seasons long past</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003811": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003811",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003812": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003812",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The weapon-smith worked it, wondrously made it,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003813": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003813",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003814": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003814",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">With swine-bodies fashioned it, that thenceforward no longer</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003815": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003815",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003816": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003816",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Brand might bite it, and battle-sword hurt it.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003817": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003817",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003818": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003818",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And that was not least of helpers in prowess</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003819": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003819",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">He has Unferth’s sword in his hand.</span> <span class=\"linenum\">70</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003820": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003820",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That Hrothgar’s spokesman had lent him when straitened;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003821": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003821",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003822": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003822",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And the hilted hand-sword was Hrunting entitled,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003823": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003823",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003824": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003824",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Old and most excellent ’mong all of the treasures;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003825": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003825",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003826": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003826",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Its blade was of iron, blotted with poison,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003827": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003827",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003828": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003828",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hardened with gore; it failed not in battle</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003829": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003829",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">75</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003830": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003830",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Any hero under heaven in hand who it brandished,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003831": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003831",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003832": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003832",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Who ventured to take the terrible journeys,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003833": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003833",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003834": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003834",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The battle-field sought; not the earliest occasion</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003835": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003835",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003836": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003836",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That deeds of daring ’twas destined to ’complish.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003837": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003837",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Unferth has little use for swords.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003838": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003838",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Ecglaf’s kinsman minded not soothly,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003839": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003839",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">80</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003840": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003840",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Exulting in strength, what erst he had spoken</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003841": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003841",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003842": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003842",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Drunken with wine, when the weapon he lent to</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003843": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003843",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003844": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003844",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A sword-hero bolder; himself did not venture</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003845": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003845",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003846": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003846",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Neath the strife of the currents his life to endanger,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003847": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003847",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003848": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003848",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To fame-deeds perform; there he forfeited glory,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003849": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003849",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">85</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003850": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003850",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Repute for his strength. Not so with the other</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003851": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003851",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003852": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003852",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">When he clad in his corslet had equipped him for battle.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003853": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003853",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003854": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003854",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\">\n</div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003855": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003855",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003856": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003856",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2 class=\"num\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000000097\">XXIII.</$link></h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003857": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003857",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003858": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003858",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2>BEOWULF’S FIGHT WITH GRENDEL’S MOTHER.</h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003859": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003859",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf makes a parting speech to Hrothgar.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003860": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003860",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Beowulf spake, Ecgtheow’s son:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003861": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003861",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003862": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003862",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">“Recall now, oh, famous kinsman of Healfdene,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003863": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003863",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003864": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003864",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Prince very prudent, now to part I am ready,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003865": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003865",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003866": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003866",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Gold-friend of earlmen, what erst we agreed on,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003867": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003867",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">If I fail, act as a kind liegelord to my thanes,</span> <span class=\"linenum\">5</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003868": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003868",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Should I lay down my life in lending thee assistance,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003869": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003869",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003870": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003870",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">When my earth-joys were over, thou wouldst evermore serve me</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003871": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003871",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003872": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003872",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In stead of a father; my faithful thanemen,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003873": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003873",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003874": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003874",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">My trusty retainers, protect thou and care for,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003875": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003875",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003876": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003876",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Fall I in battle: and, Hrothgar belovèd,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003877": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003877",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">and send Higelac the jewels thou hast given me</span> <span class=\"linenum\">10</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003878": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003878",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Send unto Higelac the high-valued jewels</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003879": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003879",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003880": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003880",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Thou to me hast allotted. The lord of the Geatmen</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003881": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003881",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003882": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003882",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">May perceive from the gold, the Hrethling may see it</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003883": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003883",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">I should like my king to know how generous a lord I found thee to be.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003884": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003884",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">When he looks on the jewels, that a gem-giver found I</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003885": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003885",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003886": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003886",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Good over-measure, enjoyed him while able.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003887": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003887",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">15</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003888": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003888",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And the ancient heirloom Unferth permit thou,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003889": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003889",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003890": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003890",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The famed one to have, the heavy-sword splendid<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000004025\">1</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003891": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003891",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003892": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003892",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The hard-edgèd weapon; with Hrunting to aid me,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003893": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003893",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003894": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003894",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">I shall gain me glory, or grim-death shall take me.”</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003895": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003895",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf is eager for the fray.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003896": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003896",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The atheling of Geatmen uttered these words and</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003897": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003897",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">20</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003898": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003898",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Heroic did hasten, not any rejoinder</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003899": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003899",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003900": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003900",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Was willing to wait for; the wave-current swallowed</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003901": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003901",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">He is a whole day reaching the bottom of the sea.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003902": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003902",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The doughty-in-battle. Then a day’s-length elapsed ere</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003903": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003903",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003904": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003904",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He was able to see the sea at its bottom.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003905": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003905",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003906": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003906",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Early she found then who fifty of winters</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003907": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003907",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">25</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003908": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003908",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The course of the currents kept in her fury,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003909": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003909",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003910": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003910",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Grisly and greedy, that the grim one’s dominion</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003911": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003911",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a> <span class=\"sidenote\">Grendel’s mother knows that some one has reached her domains.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003912": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003912",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Some one of men from above was exploring.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003913": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003913",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003914": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003914",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Forth did she grab them, grappled the warrior</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003915": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003915",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003916": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003916",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">With horrible clutches; yet no sooner she injured</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003917": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003917",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">30</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003918": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003918",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His body unscathèd: the burnie out-guarded,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003919": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003919",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003920": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003920",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That she proved but powerless to pierce through the armor,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003921": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003921",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003922": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003922",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The limb-mail locked, with loath-grabbing fingers.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003923": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003923",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003924": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003924",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The sea-wolf bare then, when bottomward came she,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003925": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003925",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">She grabs him, and bears him to her den.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003926": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003926",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The ring-prince homeward, that he after was powerless</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003927": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003927",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">35</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003928": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003928",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">(He had daring to do it) to deal with his weapons,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003929": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003929",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003930": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003930",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">But many a mere-beast tormented him swimming,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003931": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003931",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Sea-monsters bite and strike him.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003932": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003932",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Flood-beasts no few with fierce-biting tusks did</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003933": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003933",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003934": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003934",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Break through his burnie, the brave one pursued they.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003935": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003935",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003936": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003936",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The earl then discovered he was down in some cavern</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003937": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003937",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">40</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003938": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003938",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Where no water whatever anywise harmed him,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003939": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003939",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003940": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003940",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And the clutch of the current could come not anear him,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003941": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003941",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003942": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003942",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Since the roofed-hall prevented; brightness a-gleaming</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003943": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003943",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003944": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003944",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Fire-light he saw, flashing resplendent.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003945": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003945",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003946": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003946",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The good one saw then the sea-bottom’s monster,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003947": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003947",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf attacks the mother of Grendel.</span> <span class=\"linenum\">45</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003948": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003948",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The mighty mere-woman; he made a great onset</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003949": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003949",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003950": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003950",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">With weapon-of-battle, his hand not desisted</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003951": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003951",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003952": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003952",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">From striking, that war-blade struck on her head then</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003953": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003953",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003954": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003954",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A battle-song greedy. The stranger perceived then</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003955": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003955",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The sword will not bite.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003956": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003956",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The sword would not bite, her life would not injure,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003957": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003957",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">50</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003958": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003958",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">But the falchion failed the folk-prince when straitened:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003959": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003959",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003960": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003960",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Erst had it often onsets encountered,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003961": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003961",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003962": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003962",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Oft cloven the helmet, the fated one’s armor:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003963": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003963",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003964": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003964",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Twas the first time that ever the excellent jewel</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003965": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003965",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003966": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003966",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Had failed of its fame. Firm-mooded after,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003967": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003967",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">55</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003968": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003968",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Not heedless of valor, but mindful of glory,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003969": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003969",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003970": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003970",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Was Higelac’s kinsman; the hero-chief angry</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003971": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003971",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003972": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003972",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Cast then his carved-sword covered with jewels</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003973": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003973",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003974": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003974",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That it lay on the earth, hard and steel-pointed;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003975": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003975",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The hero throws down all weapons, and again trusts to his hand-grip.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003976": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003976",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He hoped in his strength, his hand-grapple sturdy.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003977": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003977",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">60</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003978": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003978",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">So any must act whenever he thinketh</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003979": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003979",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003980": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003980",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To gain him in battle glory unending,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003981": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003981",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003982": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003982",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And is reckless of living. The lord of the War-Geats</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003983": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003983",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003984": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003984",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">(He shrank not from battle) seized by the shoulder<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000004027\">2</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003985": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003985",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003986": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003986",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The mother of Grendel; then mighty in struggle</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003987": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003987",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">65</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003988": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003988",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Swung he his enemy, since his anger was kindled,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003989": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003989",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003990": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003990",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That she fell to the floor. With furious grapple</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003991": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003991",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf falls.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003992": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003992",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">She gave him requital<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000004029\">3</$link></sup> early thereafter,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003993": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003993",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003994": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003994",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And stretched out to grab him; the strongest of warriors</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003995": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003995",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003996": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003996",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Faint-mooded stumbled, till he fell in his traces,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003997": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003997",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The monster sits on him with drawn sword.</span> <span class=\"linenum\">70</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003998": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003998",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Foot-going champion. Then she sat on the hall-guest</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000003999": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000003999",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004000": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004000",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And wielded her war-knife wide-bladed, flashing,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004001": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004001",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004002": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004002",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">For her son would take vengeance, her one only bairn.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004003": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004003",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">His armor saves his life.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004004": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004004",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His breast-armor woven bode on his shoulder;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004005": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004005",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004006": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004006",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">It guarded his life, the entrance defended</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004007": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004007",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">75</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004008": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004008",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Gainst sword-point and edges. Ecgtheow’s son there</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004009": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004009",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004010": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004010",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Had fatally journeyed, champion of Geatmen,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004011": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004011",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004012": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004012",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In the arms of the ocean, had the armor not given,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004013": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004013",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004014": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004014",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Close-woven corslet, comfort and succor,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004015": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004015",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">God arranged for his escape.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004016": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004016",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And had God most holy not awarded the victory,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004017": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004017",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">80</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004018": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004018",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">All-knowing Lord; easily did heaven’s</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004019": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004019",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004020": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004020",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Ruler most righteous arrange it with justice;<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000004031\">4</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004021": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004021",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004022": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004022",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Uprose he erect ready for battle.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004023": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004023",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004024": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004024",
