        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004573": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004573",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The blade of the brand: ’twas a brave-mooded hero.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004574": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004574",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004575": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004575",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">When the warriors were ready, arrayed in their trappings,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004576": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004576",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004577": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004577",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The atheling dear to the Danemen advanced then</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004578": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004578",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004579": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004579",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">On to the dais, where the other was sitting,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000004580": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000004580",
