        "$:/plugins/twpub/b444daeafa3e6711/text/000005043": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000005043",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\"><sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000005058\">5</$link></sup>Somewhere not rarely, when the ruler has fallen,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000005044": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000005044",
