        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000005070": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000005070",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And each of the thanemen of earls that attend him,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000005071": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000005071",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000005072": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000005072",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">When he goes to the building escorting the woman,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000005073": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000005073",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000005074": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000005074",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That a noble-born Daneman the knights should be feasting:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000005075": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000005075",
