        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000005078": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000005078",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hard and ring-bright, Heathobards’ treasure,</div></div></div></div>"
        },
