        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000005080": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000005080",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">While they wielded their arms, till they misled to the battle</div></div></div></div>"
        },
