        "$:/plugins/twpub/b444daeafa3e6711/text/000005449": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000005449",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000005310\">[1]</$link> This verse B. renders, ‘<i>Now serve I again thee alone as my gracious king</i>.’</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000005450": {
            "role": "text",
