        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000005819": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000005819",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000005802\">[3]</$link> Gr. read ‘<span class=\"ang\">on feorme</span>’ (2386), rendering: <i>He there at the banquet a fatal wound received by blows of the sword.</i></div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000005820": {
