        "$:/plugins/twpub/b8ff92294eace440/text/000000607": {
            "role": "text",
            "title": "$:/plugins/twpub/b8ff92294eace440/text/000000607",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b8ff92294eace440-b0e0ab twpub-b8ff92294eace440-7fac4a twpub-b8ff92294eace440-6b4cd0\"><div class=\"chapter\"><p class=\"poem\">Life treads on life, and heart on heart;<br>\nWe press too close in church and mart<br>\nTo keep a dream or grave apart.<br></p></div></div>"
        },
        "$:/plugins/twpub/b8ff92294eace440/text/000000608": {
