        "$:/plugins/twpub/c6b43ad861391ea7/text/000000267": {
            "role": "text",
            "title": "$:/plugins/twpub/c6b43ad861391ea7/text/000000267",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c6b43ad861391ea7-0d454f twpub-c6b43ad861391ea7-5fae15 twpub-c6b43ad861391ea7-1a3ef0 twpub-c6b43ad861391ea7-6b4cd0\"><div class=\"back\"><div class=\"div1 notes\"><div class=\"divBody\"><div class=\"table\"><table>\n<tbody><tr>\n<td class=\"cellLeft cellTop c2\">Capacity</td>\n<td class=\"cellTop c2\">Name</td>\n<td class=\"cellRight cellTop c2\">Nationality</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Captain and supervisor of the fleet</td>\n<td>Juan de Cartagena</td>\n<td class=\"cellRight\"></td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Accountant</td>\n<td>Antonio de Coca</td>\n<td class=\"cellRight\"></td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Notary</td>\n<td>Hierónimo Guerra</td>\n<td class=\"cellRight\"></td>\n</tr>\n<tr>\n<td class=\"cellLeft\">His Majesty’s pilot</td>\n<td>Andres de San Martin</td>\n<td class=\"cellRight\"></td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Pilot of his Highness</td>\n<td>Juan Rodriguez de Mafra</td>\n<td class=\"cellRight\"></td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Master</td>\n<td>Juan de Elorriaga<$link class=\"noteref pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000001450\">24</$link></td>\n<td class=\"cellRight\">Guipúzcoa</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Boatswain</td>\n<td>Diego Hernandez</td>\n<td class=\"cellRight\">Sevilla</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Barber</td>\n<td>Pedro Olabarrieta<$link class=\"noteref pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000001452\">25</$link></td>\n<td class=\"cellRight\">Bilbao</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Steward</td>\n<td>Juan Ortiz de Gopegar<$link class=\"noteref pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000001454\">26</$link></td>\n<td class=\"cellRight\">Bilbao</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Calker</td>\n<td>Pedro de Bilbao</td>\n<td class=\"cellRight\">Bilbao</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Carpenter</td>\n<td>Pedro de Sabtua</td>\n<td class=\"cellRight\">Bermeo</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Calker</td>\n<td>Martin de Goytisolo</td>\n<td class=\"cellRight\">Baquio</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Cooper</td>\n<td>Joan de Oviedo</td>\n<td class=\"cellRight\">Sevilla</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Sebastian de Olarte</td>\n<td class=\"cellRight\">Bilbao</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Lope de Uguarte</td>\n<td class=\"cellRight\"></td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Joanes de Segura</td>\n<td class=\"cellRight\">Segura in Guipúzcoa</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Joan de Francia</td>\n<td class=\"cellRight\">Ruan [<i>i.e.</i>, Rouen]</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Jácome de Mecina</td>\n<td class=\"cellRight\">Mesina</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Christóbal García</td>\n<td class=\"cellRight\">From Palos</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Pero Hernandez</td>\n<td class=\"cellRight\">Rivadesella</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Antonio Rodríguez, Calderero [<i>i.e.</i>, blacksmith]</td>\n<td class=\"cellRight\">Sevilla</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Hernando de Morales<$link class=\"noteref pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000001456\">27</$link></td>\n<td class=\"cellRight\">From Moguer</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Francisco, Marinero [<i>i.e.</i>, a sailor]</td>\n<td class=\"cellRight\">Citizen of Huelva</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Francisco Ros, or Rodriguez</td>\n<td class=\"cellRight\">From Huelva</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Pedro de Laredo</td>\n<td class=\"cellRight\">Portogalete</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Simon de Asio</td>\n<td class=\"cellRight\">Axio <a class=\"x-ebookmaker-pageno\"></a></td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Gunner</td>\n<td>Master Jacques, chief gunner</td>\n<td class=\"cellRight\">From Tierra Lorena [<i>i.e.</i>, land of Lorraine]</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Gunner</td>\n<td>Rojer Dupict</td>\n<td class=\"cellRight\">Monaym</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Gunner</td>\n<td>Joan Jorge</td>\n<td class=\"cellRight\">Silvedrin</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Common seaman</td>\n<td>Luis,<$link class=\"noteref pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000001458\">28</$link> Grumete [<i>i.e.</i>, a common seaman]</td>\n<td class=\"cellRight\">Galicia</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Common seaman</td>\n<td>Martin de Aguirre</td>\n<td class=\"cellRight\">Arrigorriaga</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Common seaman</td>\n<td>Columbazo</td>\n<td class=\"cellRight\">Bolonia [<i>i.e.</i>, Bologna]</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Common seaman</td>\n<td>Lucas de Mecina</td>\n<td class=\"cellRight\">Mesina</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Common seaman</td>\n<td>Lorencio Rodriguez</td>\n<td class=\"cellRight\">From Moguer</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Common seaman</td>\n<td>Miguel</td>\n<td class=\"cellRight\">Pravia, in Astúrias</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Common seaman</td>\n<td>Joanes de Irun Iranzo</td>\n<td class=\"cellRight\">Irun Iranza in Guipúzcoa</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Common seaman</td>\n<td>Joan Ginoves</td>\n<td class=\"cellRight\">Saona</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Common seaman</td>\n<td>Joan de Orue</td>\n<td class=\"cellRight\">Munguia</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Common seaman</td>\n<td>Alonso del Puerto<$link class=\"noteref pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000001460\">29</$link></td>\n<td class=\"cellRight\">Puerto de Santa María</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Boy</td>\n<td>Diego, son of Cristóbal Garcia</td>\n<td class=\"cellRight\">From Palos</td>\n</tr>\n<tr>\n<td class=\"cellLeft cellBottom\">Boy</td>\n<td class=\"cellBottom\">Diego, son of Juan Rodriguez de Mafra</td>\n<td class=\"cellRight cellBottom\"></td>\n</tr>\n</tbody></table></div></div></div></div></div>"
