            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c6b43ad861391ea7-0d454f twpub-c6b43ad861391ea7-5fae15 twpub-c6b43ad861391ea7-1a3ef0 twpub-c6b43ad861391ea7-6b4cd0\"><div class=\"back\"><div class=\"div1 notes\"><div class=\"divBody\"><div class=\"table\"><table>\n<tbody><tr>\n<td class=\"cellLeft cellTop\">Captain and pilot of his Highness</td>\n<td class=\"cellTop\">Joan Serrano</td>\n<td class=\"cellRight cellTop\">Citizen of Sevilla</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Notary</td>\n<td>Antonio de Costa</td>\n<td class=\"cellRight\"></td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Master</td>\n<td>Baltasar Ginoves</td>\n<td class=\"cellRight\">Ribera de Génova [<i>i.e.</i>, the Genoese shore]</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Boatswain</td>\n<td>Bartolomé Prior<$link class=\"noteref pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000001524\">61</$link></td>\n<td class=\"cellRight\">San Malo</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Steward</td>\n<td>Gaspar Diaz</td>\n<td class=\"cellRight\">Isla Graciosa, in the Azores</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Calker</td>\n<td>Joan García</td>\n<td class=\"cellRight\">Génova</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Carpenter</td>\n<td>Ripart<$link class=\"noteref pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000001526\">62</$link></td>\n<td class=\"cellRight\">Bruz in Normandia [<i>i.e.</i>, Normandy]</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Antonio Flamenco [<i>i.e.</i>, a Fleming]</td>\n<td class=\"cellRight\">Enveres <a class=\"x-ebookmaker-pageno\"></a></td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Luis Martinez</td>\n<td class=\"cellRight\">Huelva</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Bartolomé García</td>\n<td class=\"cellRight\">Palos</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Joan García</td>\n<td class=\"cellRight\">Palos</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Agustin</td>\n<td class=\"cellRight\">Saona</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Bocacio Alfonso<$link class=\"noteref pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000001528\">63</$link></td>\n<td class=\"cellRight\">Bollullos</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Pedro Gascon<$link class=\"noteref pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000001530\">64</$link> [<i>i.e.</i>, a Gascon]</td>\n<td class=\"cellRight\">Burdeos [<i>i.e.</i>, Bordeaux]</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Domingo<$link class=\"noteref pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000001532\">65</$link></td>\n<td class=\"cellRight\"></td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Sailor</td>\n<td>Diego García de Trigueros</td>\n<td class=\"cellRight\">Trigueros</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Gunner</td>\n<td>Lorenzo Corrat</td>\n<td class=\"cellRight\">Talesa in Normandia [<i>i.e.</i>, Normandy]</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Gunner</td>\n<td>Joan Macia<$link class=\"noteref pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000001534\">66</$link></td>\n<td class=\"cellRight\">Troya</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Common seaman</td>\n<td>Pedro Diaz<$link class=\"noteref pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000001536\">67</$link></td>\n<td class=\"cellRight\">Huelva</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Common seaman</td>\n<td>Antonio Hernandez<$link class=\"noteref pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000001538\">68</$link></td>\n<td class=\"cellRight\">Palos</td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Common seaman</td>\n<td>Juan,<$link class=\"noteref pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000001540\">69</$link> a negro</td>\n<td class=\"cellRight\"></td>\n</tr>\n<tr>\n<td class=\"cellLeft\">Common seaman</td>\n<td>Joan Breton [<i>i.e.</i>, a Breton]</td>\n<td class=\"cellRight\">Cruesic in Bretaña [<i>i.e.</i>, Brittany]</td>\n</tr>\