        },
        "$:/plugins/twpub/c6b43ad861391ea7/text/000000925": {
            "role": "text",
            "title": "$:/plugins/twpub/c6b43ad861391ea7/text/000000925",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c6b43ad861391ea7-0d454f twpub-c6b43ad861391ea7-5fae15 twpub-c6b43ad861391ea7-1a3ef0 twpub-c6b43ad861391ea7-6b4cd0\"><div class=\"back\"><div class=\"div1 notes\"><div class=\"divBody\"><p><sup><$link class=\"pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000000164\">287</$link></sup> These last two clauses are omitted in MS. 5,650. <a class=\"x-ebookmaker-pageno\"></a></p></div></div></div></div>"
        },
        "$:/plugins/twpub/c6b43ad861391ea7/text/000000926": {
