        },
        "$:/plugins/twpub/c6b43ad861391ea7/text/000000927": {
            "role": "text",
            "title": "$:/plugins/twpub/c6b43ad861391ea7/text/000000927",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c6b43ad861391ea7-0d454f twpub-c6b43ad861391ea7-5fae15 twpub-c6b43ad861391ea7-1a3ef0 twpub-c6b43ad861391ea7-6b4cd0\"><div class=\"back\"><div class=\"div1 notes\"><div class=\"divBody\"><p><sup><$link class=\"pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000000164\">288</$link></sup> MS. 5,650 adds: “of Sainct Jacques [<i>i.e.</i>, Santiago].”</p></div></div></div></div>"
        },
        "$:/plugins/twpub/c6b43ad861391ea7/text/000000928": {
            "role": "text",
