        "$:/plugins/twpub/c6b43ad861391ea7/text/000001196": {
            "role": "text",
            "title": "$:/plugins/twpub/c6b43ad861391ea7/text/000001196",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c6b43ad861391ea7-0d454f twpub-c6b43ad861391ea7-5fae15 twpub-c6b43ad861391ea7-1a3ef0 twpub-c6b43ad861391ea7-6b4cd0\"><div class=\"back\"><div class=\"div1 notes\"><div class=\"divBody\"><p><sup><$link class=\"pginternal\" to=\"$:/plugins/twpub/c6b43ad861391ea7/text/000000171\">410</$link></sup> Stanley makes the unhappy translation “with naked daggers in their hands, which they held on their thighs.”</p></div></div></div></div>"
        },
        "$:/plugins/twpub/c6b43ad861391ea7/text/000001197": {
            "role": "text",
            "title": "$:/plugins/twpub/c6b43ad861391ea7/text/000001197",
