        "$:/plugins/twpub/c896a6cf8f21d3f4/text/000001452": {
            "role": "text",
            "title": "$:/plugins/twpub/c896a6cf8f21d3f4/text/000001452",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c896a6cf8f21d3f4-672b69 twpub-c896a6cf8f21d3f4-f1495c twpub-c896a6cf8f21d3f4-6b4cd0\"><div class=\"c13\"><div class=\"c2\">Project Gutenberg™ depends upon and cannot survive without widespread public support and donations to carry out its mission of increasing the number of public domain and licensed works that can be freely distributed in machine-readable form accessible by the widest array of equipment including outdated equipment. Many small donations ($1 to $5,000) are particularly important to maintaining tax exempt status with the IRS.</div></div></div>"
        },
        "$:/plugins/twpub/c896a6cf8f21d3f4/text/000001453": {
