        "$:/plugins/twpub/c896a6cf8f21d3f4/text/000001464": {
            "role": "text",
            "title": "$:/plugins/twpub/c896a6cf8f21d3f4/text/000001464",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c896a6cf8f21d3f4-672b69 twpub-c896a6cf8f21d3f4-f1495c twpub-c896a6cf8f21d3f4-6b4cd0\"><div class=\"c13\"><div class=\"c2\">Professor Michael S. Hart was the originator of the Project Gutenberg™ concept of a library of electronic works that could be freely shared with anyone. For forty years, he produced and distributed Project Gutenberg™ eBooks with only a loose network of volunteer support.</div></div></div>"
        },
        "$:/plugins/twpub/c896a6cf8f21d3f4/text/000001465": {
