        "$:/plugins/twpub/ca7bc903db05ca3e/text/000000021": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000000021",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><p class=\"fm3\">DEDICATED<br>\nTO THAT SMALL PORTION OF THE BRITISH PUBLIC<br>\nWHICH TAKES ENLIGHTENED INTEREST IN<br>\nSTUDYING THE MANNERS AND CUSTOMS<br>\nOF THE OLDEN EAST.</p></div>"
        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000000022": {
