        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000000038": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000000038",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><ol><li value=\"5\">The Rasmanjari, or the sprout of love.</li></ol></div>"
        },
