        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000000040": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000000040",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><ol><li value=\"6\">The Anunga Runga, or the stage of love; also called Kamaledhiplava, or a boat in the ocean of love.</li></ol></div>"
        },
