        "$:/plugins/twpub/ca7bc903db05ca3e/text/000000261": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000000261",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><ol><li value=\"40\">Gardening; knowledge of treating the diseases of trees and plants, of nourishing them, and determining their ages.</li></ol></div>"
        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000000262": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000000262",
            "type": "text/vnd.twpub",
