        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000000591": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000000591",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><ul class=\"c4\"><li value=\"2\"><i>Vrikshadhirudhaka</i>, or climbing a tree.</li></ul></div>"
        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000000592": {
