        "$:/plugins/twpub/ca7bc903db05ca3e/text/000000640": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000000640",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><ul class=\"c4\"><li value=\"1\">The straight kiss.</li></ul></div>"
        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000000641": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000000641",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><ul class=\"c4\">\n</ul></div>"
