        "$:/plugins/twpub/ca7bc903db05ca3e/text/000000742": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000000742",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><p>(1). The biting which is shown only by the excessive redness of the skin that is bitten, is called the &quot;hidden bite.&quot;</p></div>"
        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000000743": {
