        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000002166": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000002166",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><div class=\"footnotes\"><div class=\"footnote\"><p><a></a><$link class=\"pginternal\" to=\"$:/plugins/twpub/ca7bc903db05ca3e/text/000000180\"><span class=\"label\">[12]</span></$link> The proviso of being married applies to all the teachers.</p></div></div></div>"
        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000002167": {
