        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000002174": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000002174",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><div class=\"footnotes\"><div class=\"footnote\"><p><a></a><$link class=\"pginternal\" to=\"$:/plugins/twpub/ca7bc903db05ca3e/text/000000316\"><span class=\"label\">[14]</span></$link> Gift is peculiar to a Brahman, conquest to a Kshatrya, while purchase, deposit, and other means of acquiring wealth belongs to the Vaishya.</p></div></div></div>"
        },
