        "$:/plugins/twpub/ca7bc903db05ca3e/text/000002226": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000002226",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><div class=\"footnotes\"><div class=\"footnote\"><p><a></a><$link class=\"pginternal\" to=\"$:/plugins/twpub/ca7bc903db05ca3e/text/000000342\"><span class=\"label\">[27]</span></$link> A seat in the form of the letter T.</p></div></div></div>"
        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000002227": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000002227",
