        "$:/plugins/twpub/ca7bc903db05ca3e/text/000002302": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000002302",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><div class=\"footnotes\"><div class=\"footnote\"><p><a></a><$link class=\"pginternal\" to=\"$:/plugins/twpub/ca7bc903db05ca3e/text/000001044\"><span class=\"label\">[46]</span></$link> A disease consisting of any glandular enlargement in any part of the body.</p></div></div></div>"
        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000002303": {
