        "$:/plugins/twpub/ca7bc903db05ca3e/text/000002306": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000002306",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><div class=\"footnotes\"><div class=\"footnote\"><p><a></a><$link class=\"pginternal\" to=\"$:/plugins/twpub/ca7bc903db05ca3e/text/000001054\"><span class=\"label\">[47]</span></$link> A woman, the palms of whose hands and the soles of whose feet are always perspiring.</p></div></div></div>"
        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000002307": {
            "role": "text",
