        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000002372": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000002372",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><div class=\"footnotes\"><div class=\"footnote\"><p><a></a><$link class=\"pginternal\" to=\"$:/plugins/twpub/ca7bc903db05ca3e/text/000001453\"><span class=\"label\">[62]</span></$link> It is supposed that storms, earthquakes, famines and pestilent diseases are here alluded to.</p></div></div></div>"
        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000002373": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000002373",
