        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000002454": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000002454",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><div class=\"transnote\"><p>All occurrences of &quot;i.e.&quot; have been italicized for consistency.</p></div></div>"
        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000002455": {
