        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000002456": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000002456",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><div class=\"transnote\"><p>On Page <$link class=\"pginternal\" to=\"$:/plugins/twpub/ca7bc903db05ca3e/text/000001492\">128</$link> there is a paragraph listed as &quot;(2)&quot;. However, there is no preceding paragraph listed as &quot;(1)&quot;. This is unchanged.</p></div></div>"
        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000002457": {
            "role": "text",
            "title": "$:/plugins/twpub/ca7bc903db05ca3e/text/000002457",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-ca7bc903db05ca3e-e24e33 twpub-ca7bc903db05ca3e-ee4fbb twpub-ca7bc903db05ca3e-6b4cd0\"><div class=\"transnote\">\n</div></div>"
        },
        "$:/plugins/twpub/ca7bc903db05ca3e/text/000002458": {
            "role": "text",
