            "title": "$:/plugins/twpub/cffe7d2da006e2fe/text/000000009",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-cffe7d2da006e2fe-fe7ed5 twpub-cffe7d2da006e2fe-7be0cc twpub-cffe7d2da006e2fe-6b4cd0\"><table class=\"c1\">\n<tbody><tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000011\">THE TURN OF THE SCREW</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000068\">I</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000086\">II</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000135\">III</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000156\">IV</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000165\">V</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000251\">VI</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000295\">VII</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000363\">VIII</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000397\">IX</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000406\">X</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000420\">XI</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000440\">XII</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000472\">XIII</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000479\">XIV</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000526\">XV</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000534\">XVI</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000583\">XVII</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000632\">XVIII</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000658\">XIX</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000689\">XX</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000701\">XXI</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000774\">XXII</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000788\">XXIII</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/cffe7d2da006e2fe/text/000000818\">XXIV</$link></td>\n</tr>\n</tbody></table></div>"
