            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-d6cbf7b38141b6ad-3e1261 twpub-d6cbf7b38141b6ad-3bb953 twpub-d6cbf7b38141b6ad-6b4cd0\"><table>\n<tbody><tr>\n<td><$link class=\"c6 pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000017\">NOTES FROM THE UNDERGROUND</$link><br>\n<br></td>\n</tr>\n<tr>\n<td><$link class=\"c6 pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000022\">PART I Underground</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000025\">I</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000048\">II</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000059\">III</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000076\">IV</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000083\">V</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000088\">VI</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000095\">VII</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000102\">VIII</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000117\">IX</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000126\">X</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000139\">XI</$link><br>\n<br></td>\n</tr>\n<tr>\n<td><$link class=\"c6 pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000170\">PART II À Propos of the Wet Snow</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000175\">I</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000236\">II</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000251\">III</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000334\">IV</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000535\">V</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000590\">VI</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000833\">VII</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000862\">VIII</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000000957\">IX</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/d6cbf7b38141b6ad/text/000001030\">X</$link></td>\n</tr>\n</tbody></table></div>"
