        },
        "$:/plugins/twpub/dbf9316ff4e8b134/text/000000011": {
            "role": "text",
            "title": "$:/plugins/twpub/dbf9316ff4e8b134/text/000000011",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-dbf9316ff4e8b134-2d299a twpub-dbf9316ff4e8b134-9a4570 twpub-dbf9316ff4e8b134-6b4cd0\"><h2>by H. G. Wells</h2></div>"
        },
        "$:/plugins/twpub/dbf9316ff4e8b134/text/000000012": {
            "role": "text",
            "title": "$:/plugins/twpub/dbf9316ff4e8b134/text/000000012",
