        "$:/plugins/twpub/e8801f1d959ec96b/text/000000015": {
            "role": "text",
            "title": "$:/plugins/twpub/e8801f1d959ec96b/text/000000015",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-e8801f1d959ec96b-0371ff twpub-e8801f1d959ec96b-05227b twpub-e8801f1d959ec96b-6b4cd0\"><table>\n<tbody><tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000000019\">THE PURLOINED LETTER</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000000272\">THE THOUSAND-AND-SECOND TALE OF SCHEHERAZADE</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000000425\">A DESCENT INTO THE MAELSTROM.</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000000534\">VON KEMPELEN AND HIS DISCOVERY</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000000571\">MESMERIC REVELATION</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000000726\">THE FACTS IN THE CASE OF M. VALDEMAR</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000000833\">THE BLACK CAT.</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000000896\">THE FALL OF THE HOUSE OF USHER</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000000993\">SILENCE—A FABLE</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000001024\">THE MASQUE OF THE RED DEATH.</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000001055\">THE CASK OF AMONTILLADO.</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000001236\">THE IMP OF THE PERVERSE</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000001271\">THE ISLAND OF THE FAY</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000001302\">THE ASSIGNATION</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000001377\">THE PIT AND THE PENDULUM</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000001460\">THE PREMATURE BURIAL</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000001555\">THE DOMAIN OF ARNHEIM</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000001618\">LANDOR’S COTTAGE</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000001695\">WILLIAM WILSO</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000001810\">THE TELL-TALE HEART.</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000001849\">BERENICE</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000001894\">ELEONORA</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/e8801f1d959ec96b/text/000001931\">NOTES TO THE SECOND VOLUME</$link></td>\n</tr>\n</tbody></table></div>"
        },
        "$:/plugins/twpub/e8801f1d959ec96b/text/000000016": {
