        },
        "$:/plugins/twpub/e8801f1d959ec96b/text/000002028": {
            "role": "text",
            "title": "$:/plugins/twpub/e8801f1d959ec96b/text/000002028",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-e8801f1d959ec96b-0371ff twpub-e8801f1d959ec96b-05227b twpub-e8801f1d959ec96b-6b4cd0\"><div class=\"chapter\">\n</div></div>"
